from PIL import Image
import io
import base64

def is_base64_image(base64_str):
    try:
        # 解码 Base64 编码的字符串
        decoded = base64.b64decode(base64_str)
        # 尝试使用 Pillow 打开解码后的数据作为图像
        img = Image.open(io.BytesIO(decoded))
        # 检查是否成功打开并识别为图像
        img.verify()
        return True
    except Exception as e:
        return False

# 示例 Base64 编码的图片数据
base64_image_data = ""


# 检测 Base64 编码是否有效且原本为图片数据
if is_base64_image(base64_image_data):
    print("这是有效的 Base64 编码，且原本为图片数据。")
else:
    print("这不是有效的 Base64 编码，或者原本不是图片数据。")